export declare const pdfStyles = "\n  /* ========================================\n     RESET & BASE STYLES\n     ======================================== */\n  * {\n    margin: 0;\n    padding: 0;\n    box-sizing: border-box;\n  }\n\n  html, body {\n    width: 210mm;\n    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Helvetica, Arial, sans-serif;\n    font-size: 11pt;\n    line-height: 1.6;\n    color: #1F2937;\n    background: #FFFFFF;\n  }\n\n  /* ========================================\n     TYPOGRAPHY\n     ======================================== */\n  h1 {\n    font-size: 24pt;\n    font-weight: 700;\n    line-height: 1.2;\n    color: #0066CC;\n    margin-bottom: 16pt;\n  }\n\n  h2 {\n    font-size: 18pt;\n    font-weight: 600;\n    line-height: 1.3;\n    color: #1F2937;\n    margin-top: 20pt;\n    margin-bottom: 12pt;\n  }\n\n  h3 {\n    font-size: 14pt;\n    font-weight: 600;\n    line-height: 1.3;\n    color: #374151;\n    margin-top: 16pt;\n    margin-bottom: 8pt;\n  }\n\n  h4 {\n    font-size: 12pt;\n    font-weight: 600;\n    line-height: 1.3;\n    color: #4B5563;\n    margin-top: 12pt;\n    margin-bottom: 6pt;\n  }\n\n  p {\n    margin-bottom: 12pt;\n    text-align: justify;\n  }\n\n  strong {\n    font-weight: 600;\n    color: #111827;\n  }\n\n  em {\n    font-style: italic;\n  }\n\n  /* ========================================\n     COVER PAGE\n     ======================================== */\n  .cover-page {\n    display: flex;\n    flex-direction: column;\n    align-items: center;\n    justify-content: center;\n    min-height: 100vh;\n    text-align: center;\n    padding: 40pt;\n  }\n\n  .cover-logo {\n    max-width: 200pt;\n    max-height: 80pt;\n    margin-bottom: 40pt;\n  }\n\n  .cover-title {\n    font-size: 32pt;\n    font-weight: 700;\n    color: #0066CC;\n    margin-bottom: 16pt;\n    line-height: 1.2;\n  }\n\n  .cover-subtitle {\n    font-size: 20pt;\n    font-weight: 400;\n    color: #4B5563;\n    margin-bottom: 40pt;\n  }\n\n  .cover-meta {\n    font-size: 12pt;\n    color: #6B7280;\n    margin-top: 60pt;\n  }\n\n  .cover-meta p {\n    margin-bottom: 6pt;\n  }\n\n  /* ========================================\n     PAGE LAYOUT\n     ======================================== */\n\n   .content-wrapper {\n    padding: 8mm 8mm;\n  }\n\n  .page {\n    padding: 8mm 8mm;\n  }\n\n  .page-break {\n    page-break-after: always;\n    break-after: page;\n  }\n\n  .page-header {\n    display: flex;\n    justify-content: space-between;\n    align-items: center;\n    padding-bottom: 12pt;\n    margin-bottom: 20pt;\n    border-bottom: 1pt solid #E5E7EB;\n  }\n\n  .page-header-logo {\n    max-height: 30pt;\n  }\n\n  .page-header-title {\n    font-size: 10pt;\n    color: #6B7280;\n  }\n\n  .page-footer {\n    position: fixed;\n    bottom: 20mm;\n    left: 25mm;\n    right: 25mm;\n    text-align: center;\n    font-size: 9pt;\n    color: #9CA3AF;\n    padding-top: 12pt;\n    border-top: 1pt solid #E5E7EB;\n  }\n\n  /* ========================================\n     SECTIONS\n     ======================================== */\n  .section {\n    margin-bottom: 32pt;\n    page-break-inside: avoid;\n  }\n\n  .section-header {\n    margin-bottom: 20pt;\n    padding-bottom: 10pt;\n    border-bottom: 2pt solid #0066CC;\n  }\n\n  .section-number {\n    display: inline-block;\n    background: #0066CC;\n    color: #FFFFFF;\n    font-size: 14pt;\n    font-weight: 700;\n    padding: 6pt 14pt;\n    border-radius: 4pt;\n    margin-right: 10pt;\n  }\n\n  .section-title {\n    display: inline-block;\n    font-size: 20pt;\n    font-weight: 600;\n    color: #1F2937;\n  }\n\n  .subsection {\n    margin-bottom: 30pt;\n  }\n  \n  /* ========================================\n     CARD COMPONENTS\n     ======================================== */\n  .card {\n    background: #F9FAFB;\n    border: 1pt solid #E5E7EB;\n    border-radius: 6pt;\n    padding: 16pt;\n    margin-bottom: 12pt;\n  }\n  \n  .card-header {\n    margin-bottom: 12pt;\n    padding-bottom: 8pt;\n    border-bottom: 1pt solid #E5E7EB;\n  }\n  \n  .card-title {\n    font-size: 13pt;\n    font-weight: 600;\n    color: #1F2937;\n  }\n  \n  .card-grid {\n    display: grid;\n    grid-template-columns: repeat(2, 1fr);\n    gap: 12pt;\n    margin: 16pt 0;\n  }\n  \n  .card-grid-3 {\n    display: grid;\n    grid-template-columns: repeat(3, 1fr);\n    gap: 10pt;\n    margin: 16pt 0;\n  }\n  \n  /* ========================================\n     BADGES & INDICATORS\n     ======================================== */\n  .badge {\n    display: inline-block;\n    padding: 3pt 8pt;\n    border-radius: 12pt;\n    font-size: 9pt;\n    font-weight: 600;\n    margin-right: 6pt;\n  }\n  \n  .badge-primary {\n    background: #DBEAFE;\n    color: #1E40AF;\n  }\n  \n  .badge-success {\n    background: #D1FAE5;\n    color: #065F46;\n  }\n  \n  .badge-warning {\n    background: #FEF3C7;\n    color: #92400E;\n  }\n  \n  .badge-info {\n    background: #E0E7FF;\n    color: #3730A3;\n  }\n  \n  .demand-indicator {\n    display: inline-block;\n    padding: 2pt 10pt;\n    border-radius: 4pt;\n    font-size: 9pt;\n    font-weight: 600;\n  }\n  \n  .demand-very-high {\n    background: #10B981;\n    color: #FFFFFF;\n  }\n  \n  .demand-high {\n    background: #34D399;\n    color: #FFFFFF;\n  }\n  \n  .demand-medium {\n    background: #FBBF24;\n    color: #FFFFFF;\n  }\n\n  /* ========================================\n     TABLES\n     ======================================== */\n  table {\n    width: 100%;\n    border-collapse: collapse;\n    margin: 16pt 0;\n    font-size: 10pt;\n    page-break-inside: auto;\n  }\n\n  thead {\n    background: linear-gradient(135deg, #EBF5FF 0%, #DBEAFE 100%);\n  }\n\n  thead th {\n    padding: 10pt 12pt;\n    text-align: left;\n    font-weight: 600;\n    color: #1F2937;\n    border: 1pt solid #E5E7EB;\n    font-size: 11pt;\n  }\n\n  tbody td {\n    padding: 10pt 12pt;\n    border: 1pt solid #E5E7EB;\n    vertical-align: top;\n    line-height: 1.5;\n  }\n\n  tbody tr {\n    page-break-inside: avoid;\n  }\n\n  tbody tr:nth-child(even) {\n    background: #F9FAFB;\n  }\n\n  tbody tr:nth-child(odd) {\n    background: #FFFFFF;\n  }\n  \n  tbody tr:hover {\n    background: #EBF5FF;\n  }\n\n  /* ========================================\n     LISTS\n     ======================================== */\n  ul, ol {\n    margin-left: 20pt;\n    margin-bottom: 12pt;\n  }\n\n  li {\n    margin-bottom: 6pt;\n  }\n\n  ul.checkmark-list {\n    list-style: none;\n    margin-left: 0;\n  }\n\n  ul.checkmark-list li:before {\n    content: \"\u2713 \";\n    color: #10B981;\n    font-weight: 700;\n    margin-right: 6pt;\n  }\n\n  /* ========================================\n     TABLE OF CONTENTS\n     ======================================== */\n  .toc {\n    margin: 40pt 0;\n  }\n\n  .toc-title {\n    font-size: 24pt;\n    font-weight: 700;\n    color: #0066CC;\n    margin-bottom: 24pt;\n    text-align: center;\n  }\n\n  .toc-item {\n    display: flex;\n    justify-content: space-between;\n    padding: 8pt 0;\n    border-bottom: 1pt dotted #D1D5DB;\n    font-size: 11pt;\n  }\n\n  .toc-item-title {\n    flex: 1;\n    color: #374151;\n  }\n\n  .toc-item-page {\n    color: #6B7280;\n    font-weight: 600;\n    margin-left: 12pt;\n  }\n\n  /* ========================================\n     VISA PATHWAY CARDS\n     ======================================== */\n  .visa-pathway {\n    background: #F9FAFB;\n    border: 1pt solid #E5E7EB;\n    border-left: 4pt solid #0066CC;\n    padding: 16pt;\n    margin-bottom: 16pt;\n    border-radius: 4pt;\n  }\n\n  .visa-pathway-title {\n    font-size: 14pt;\n    font-weight: 600;\n    color: #0066CC;\n    margin-bottom: 8pt;\n  }\n\n  .visa-pathway-subclass {\n    font-size: 12pt;\n    color: #6B7280;\n    margin-bottom: 12pt;\n  }\n\n  .visa-requirement {\n    margin-bottom: 6pt;\n  }\n\n  .visa-requirement-label {\n    font-weight: 600;\n    color: #374151;\n  }\n\n  .visa-requirement-value {\n    color: #4B5563;\n  }\n\n  /* ========================================\n     TIMELINE\n     ======================================== */\n  .timeline {\n    margin: 20pt 0;\n  }\n\n  .timeline-phase {\n    position: relative;\n    padding-left: 30pt;\n    margin-bottom: 20pt;\n    border-left: 3pt solid #0066CC;\n    page-break-inside: avoid;\n    background: #F9FAFB;\n    padding: 16pt 16pt 16pt 30pt;\n    border-radius: 4pt;\n  }\n\n  .timeline-phase:before {\n    content: \"\";\n    position: absolute;\n    left: -8pt;\n    top: 16pt;\n    width: 14pt;\n    height: 14pt;\n    background: #0066CC;\n    border-radius: 50%;\n    border: 3pt solid #FFFFFF;\n  }\n\n  .timeline-phase-name {\n    font-size: 13pt;\n    font-weight: 600;\n    color: #1F2937;\n    margin-bottom: 6pt;\n  }\n\n  .timeline-phase-duration {\n    font-size: 10pt;\n    color: #6B7280;\n    margin-bottom: 10pt;\n    font-style: italic;\n  }\n\n  .timeline-steps {\n    list-style: none;\n    margin-left: 0;\n  }\n\n  .timeline-steps li {\n    padding-left: 16pt;\n    margin-bottom: 6pt;\n    position: relative;\n    font-size: 10pt;\n  }\n\n  .timeline-steps li:before {\n    content: \"\u2192\";\n    position: absolute;\n    left: 0;\n    color: #0066CC;\n    font-weight: 700;\n  }\n\n  /* ========================================\n     COMPANY LIST & CARDS\n     ======================================== */\n  .company-tier {\n    margin-bottom: 24pt;\n    page-break-inside: avoid;\n  }\n\n  .company-tier-header {\n    background: linear-gradient(135deg, #EBF5FF 0%, #DBEAFE 100%);\n    padding: 10pt 14pt;\n    margin-bottom: 14pt;\n    border-left: 4pt solid #0066CC;\n    border-radius: 4pt;\n  }\n\n  .company-tier-title {\n    font-size: 14pt;\n    font-weight: 600;\n    color: #0066CC;\n    margin-bottom: 4pt;\n  }\n\n  .company-tier-description {\n    font-size: 10pt;\n    color: #6B7280;\n    margin-top: 4pt;\n  }\n\n  .company-list {\n    list-style: none;\n    margin-left: 0;\n    display: grid;\n    grid-template-columns: repeat(2, 1fr);\n    gap: 10pt;\n  }\n\n  .company-item {\n    padding: 10pt 12pt;\n    background: #F9FAFB;\n    border: 1pt solid #E5E7EB;\n    border-left: 3pt solid #0066CC;\n    border-radius: 4pt;\n    page-break-inside: avoid;\n  }\n\n  .company-name {\n    font-weight: 600;\n    color: #1F2937;\n    font-size: 11pt;\n    display: block;\n    margin-bottom: 4pt;\n  }\n\n  .company-location {\n    font-size: 9pt;\n    color: #6B7280;\n    background: #E5E7EB;\n    padding: 2pt 6pt;\n    border-radius: 8pt;\n    display: inline-block;\n    margin-bottom: 6pt;\n  }\n\n  .company-description {\n    font-size: 10pt;\n    color: #4B5563;\n    margin-top: 4pt;\n    line-height: 1.4;\n  }\n  \n  /* ========================================\n     SECTION 6: SALARY VARIATION\n     ======================================== */\n  .salary-variation-intro {\n    font-size: 11pt;\n    color: #475569;\n    margin-bottom: 20pt;\n    line-height: 1.6;\n  }\n\n  /* ========================================\n     CITY CARDS & SALARY VISUALIZATION\n     ======================================== */\n  .city-card {\n    background: #FFFFFF;\n    border: 1pt solid #E2E8F0;\n    border-left: 3pt solid #0066CC;\n    border-radius: 6pt;\n    padding: 14pt;\n    margin-bottom: 14pt;\n    page-break-inside: avoid;\n  }\n  \n  .city-card-header {\n    margin-bottom: 12pt;\n    padding-bottom: 8pt;\n    border-bottom: 1pt solid #E2E8F0;\n  }\n  \n  .city-card-header-content {\n    flex: 1;\n  }\n  \n  .city-name {\n    font-size: 14pt;\n    font-weight: 600;\n    color: #1E293B;\n  }\n  \n  .city-card-subtitle {\n    font-size: 9pt;\n    color: #64748B;\n    margin-top: 4pt;\n    margin-bottom: 0;\n  }\n  \n  .city-card-salary-block {\n    margin-bottom: 16pt;\n    padding: 10pt 12pt;\n    background: rgba(0, 102, 204, 0.04);\n    border-radius: 4pt;\n  }\n  \n  .salary-range {\n    margin: 8pt 0;\n  }\n  \n  .salary-range-row {\n    display: flex;\n    align-items: center;\n    gap: 10pt;\n  }\n  \n  .salary-label {\n    font-size: 10pt;\n    font-weight: 600;\n    color: #475569;\n    display: block;\n    margin-bottom: 4pt;\n  }\n  \n  .salary-value {\n    font-size: 12pt;\n    font-weight: 700;\n    color: #0066CC;\n  }\n  \n  .salary-premium-badge {\n    background: #F1F5F9;\n    color: #0066CC;\n    border: 1pt solid #CBD5E1;\n    padding: 2pt 8pt;\n    border-radius: 10pt;\n    font-size: 8pt;\n    font-weight: 600;\n  }\n  \n  .salary-bar {\n    height: 8pt;\n    background: #E2E8F0;\n    border-radius: 4pt;\n    margin: 6pt 0;\n    overflow: hidden;\n  }\n  \n  .salary-bar-fill {\n    height: 100%;\n    background: #0066CC;\n    border-radius: 4pt;\n  }\n  \n  .city-details {\n    display: grid;\n    grid-template-columns: repeat(2, 1fr);\n    gap: 8pt 16pt;\n    margin-top: 12pt;\n  }\n  \n  .city-detail-item {\n    font-size: 9pt;\n  }\n  \n  .city-detail-label {\n    color: #64748B;\n    margin-bottom: 2pt;\n    text-transform: lowercase;\n  }\n  \n  .city-detail-label::first-letter {\n    text-transform: uppercase;\n  }\n  \n  .city-detail-value {\n    font-weight: 600;\n    color: #1E293B;\n  }\n  \n  .city-detail-value-accent {\n    color: #0066CC;\n  }\n  \n  .city-notes {\n    margin-top: 12pt;\n    padding: 10pt 12pt;\n    background: rgba(0, 102, 204, 0.06);\n    border-left: 3pt solid #0066CC;\n    border-radius: 0 4pt 4pt 0;\n  }\n  \n  .city-notes-text {\n    font-size: 9pt;\n    color: #334155;\n    margin-bottom: 0;\n    line-height: 1.5;\n  }\n\n  /* ========================================\n     THANK YOU PAGE\n     ======================================== */\n  .thank-you-page {\n    padding: 40pt;\n    text-align: center;\n    min-height: 100vh;\n    display: flex;\n    flex-direction: column;\n    justify-content: center;\n  }\n\n  .thank-you-logo-container {\n    text-align: center;\n    margin-bottom: 40pt;\n    width: 100%;\n  }\n\n  .thank-you-logo {\n    max-width: 300pt;\n    max-height: 120pt;\n    height: auto;\n    width: auto;\n    object-fit: contain;\n  }\n\n  .about-worldvisa-logo {\n    max-width: 24pt;\n    max-height: 24pt;\n    height: auto;\n    width: auto;\n    object-fit: contain;\n    flex-shrink: 0;\n  }\n\n  .thank-you-hero {\n    background: linear-gradient(135deg, #EBF5FF 0%, #DBEAFE 100%);\n    padding: 32pt;\n    border-radius: 12pt;\n    margin-bottom: 32pt;\n  }\n\n  .thank-you-heading {\n    font-size: 28pt;\n    font-weight: 700;\n    color: #0066CC;\n    margin-bottom: 16pt;\n    line-height: 1.2;\n  }\n\n  .thank-you-message {\n    font-size: 14pt;\n    color: #374151;\n    margin-bottom: 24pt;\n    line-height: 1.8;\n  }\n\n  .about-worldvisa {\n    text-align: left;\n    margin: 24pt 0;\n    padding: 24pt;\n    background: #FFFFFF;\n    border: 2pt solid #E5E7EB;\n    border-radius: 8pt;\n  }\n\n  .about-worldvisa h3 {\n    color: #0066CC;\n    margin-top: 0;\n    font-size: 16pt;\n  }\n\n  .contact-info {\n    margin: 24pt 0;\n    text-align: left;\n    background: #F9FAFB;\n    padding: 20pt;\n    border-radius: 8pt;\n  }\n\n  .contact-item {\n    margin-bottom: 10pt;\n    font-size: 11pt;\n    display: flex;\n    align-items: center;\n  }\n\n  .cta-button {\n    display: inline-block;\n    background: linear-gradient(135deg, #0066CC 0%, #3B82F6 100%);\n    color: #FFFFFF !important;\n    padding: 14pt 40pt;\n    font-size: 14pt;\n    font-weight: 600;\n    text-decoration: none;\n    border-radius: 8pt;\n    margin: 28pt 0;\n    box-shadow: 0 4pt 12pt rgba(0, 102, 204, 0.3);\n    cursor: pointer;\n    transition: opacity 0.2s;\n  }\n\n  .cta-button:hover {\n    opacity: 0.9;\n  }\n\n  .cta-button:visited {\n    color: #FFFFFF !important;\n  }\n\n  .footer-text {\n    margin-top: 40pt;\n    font-size: 10pt;\n    color: #6B7280;\n  }\n  \n  .trust-indicators {\n    display: grid;\n    grid-template-columns: repeat(3, 1fr);\n    gap: 16pt;\n    margin: 24pt 0;\n  }\n  \n  .trust-item {\n    text-align: center;\n    padding: 16pt;\n    background: #F9FAFB;\n    border-radius: 6pt;\n  }\n  \n  .trust-number {\n    font-size: 24pt;\n    font-weight: 700;\n    color: #0066CC;\n    display: block;\n    margin-bottom: 4pt;\n  }\n  \n  .trust-label {\n    font-size: 10pt;\n    color: #6B7280;\n  }\n\n  /* ========================================\n     TABLE STYLES\n     ======================================== */\n  table {\n    width: 100%;\n    border-collapse: collapse;\n    margin: 20pt 0;\n  }\n\n  thead {\n    background: linear-gradient(135deg, #EBF5FF 0%, #DBEAFE 100%);\n  }\n\n  th {\n    padding: 16pt 20pt;\n    font-weight: 600;\n    font-size: 13pt;\n    color: #1F2937;\n    border: 1pt solid #E5E7EB;\n    text-align: left;\n  }\n\n  tbody tr {\n    border-bottom: 1pt solid #E5E7EB;\n  }\n\n  tbody tr:nth-child(even) {\n    background: #F9FAFB;\n  }\n\n  tbody tr:nth-child(odd) {\n    background: #FFFFFF;\n  }\n\n  td {\n    padding: 18pt 20pt;\n    border: 1pt solid #E5E7EB;\n    font-size: 12pt;\n    color: #374151;\n    vertical-align: middle;\n  }\n\n  /* ========================================\n     UTILITY CLASSES\n     ======================================== */\n  .text-center {\n    text-align: center;\n  }\n\n  .text-right {\n    text-align: right;\n  }\n\n  .mb-small {\n    margin-bottom: 8pt;\n  }\n\n  .mb-medium {\n    margin-bottom: 16pt;\n  }\n\n  .mb-large {\n    margin-bottom: 24pt;\n  }\n\n  .color-primary {\n    color: #0066CC;\n  }\n\n  .color-success {\n    color: #10B981;\n  }\n\n  .color-gray {\n    color: #6B7280;\n  }\n\n  /* ========================================\n     PRINT OPTIMIZATION\n     ======================================== */\n  @media print {\n    .page-break {\n      page-break-after: always;\n    }\n\n    .no-print {\n      display: none;\n    }\n\n    a {\n      text-decoration: none;\n      color: inherit;\n    }\n\n    table {\n      page-break-inside: avoid;\n    }\n\n    .visa-pathway {\n      page-break-inside: avoid;\n    }\n\n    .company-tier {\n      page-break-inside: avoid;\n    }\n  }\n";
