export declare const pdfStyles = "\n  /* ========================================\n     RESET & BASE STYLES\n     ======================================== */\n  * {\n    margin: 0;\n    padding: 0;\n    box-sizing: border-box;\n  }\n\n  html, body {\n    width: 210mm;\n    font-family: 'Segoe UI', -apple-system, BlinkMacSystemFont, Helvetica, Arial, sans-serif;\n    font-size: 14pt;\n    line-height: 1.6;\n    color: #1F2937;\n    background: #FFFFFF;\n  }\n\n  /* ========================================\n     TYPOGRAPHY - Tight, professional spacing\n     ======================================== */\n  h1 {\n    font-size: 20pt;\n    font-weight: 700;\n    line-height: 1.2;\n    color: #111827;\n    margin-top: 0;\n    margin-bottom: 10pt;\n  }\n\n  h2 {\n    font-size: 18pt;\n    font-weight: 600;\n    line-height: 1.25;\n    color: #1F2937;\n    margin-top: 14pt;\n    margin-bottom: 8pt;\n  }\n\n  h3 {\n    font-size: 16pt;\n    font-weight: 600;\n    line-height: 1.3;\n    color: #374151;\n    margin-top: 10pt;\n    margin-bottom: 6pt;\n  }\n\n  h4 {\n    font-size: 14pt;\n    font-weight: 600;\n    line-height: 1.3;\n    color: #4B5563;\n    margin-top: 8pt;\n    margin-bottom: 4pt;\n  }\n\n  p {\n    margin-bottom: 6pt;\n    text-align: left;\n    orphans: 3;\n    widows: 3;\n  }\n\n  strong {\n    font-weight: 600;\n    color: #111827;\n  }\n\n  em {\n    font-style: italic;\n  }\n\n  /* ========================================\n     COVER PAGE\n     ======================================== */\n  .cover-page {\n    display: flex;\n    flex-direction: column;\n    min-height: 100vh;\n    padding: 0;\n    margin: 0;\n    width: 100%;\n  }\n\n  .cover-logo {\n    max-width: 200pt;\n    max-height: 80pt;\n    margin-bottom: 40pt;\n  }\n\n  /* ========================================\n     PAGE LAYOUT - Compact padding\n     ======================================== */\n  .content-wrapper {\n    padding: 0mm 6mm;\n  }\n\n  .page {\n    padding: 8mm 6mm;\n  }\n\n  .page-break {\n    page-break-after: always;\n    break-after: page;\n  }\n\n  .page-header {\n    display: flex;\n    justify-content: space-between;\n    align-items: center;\n    padding-bottom: 8pt;\n    margin-bottom: 14pt;\n    border-bottom: 0.5pt solid #E5E7EB;\n  }\n\n  .page-header-logo {\n    max-height: 24pt;\n  }\n\n  .page-header-title {\n    font-size: 9pt;\n    color: #9CA3AF;\n    font-weight: 500;\n  }\n\n  .page-footer {\n    position: fixed;\n    bottom: 15mm;\n    left: 15mm;\n    right: 15mm;\n    text-align: center;\n    font-size: 8pt;\n    color: #9CA3AF;\n    padding-top: 8pt;\n    border-top: 0.5pt solid #E5E7EB;\n  }\n\n  /* ========================================\n     SECTIONS - Tight, no wasted space\n     ======================================== */\n  .section {\n    margin-bottom: 8pt;\n  }\n\n  .section-header {\n    margin-top: 0;\n    margin-bottom: 12pt;\n    padding-bottom: 6pt;\n    border-bottom: 2pt solid #1B2A4A;\n    display: flex;\n    align-items: center;\n    gap: 10pt;\n  }\n\n  .section-number {\n    display: inline-flex;\n    align-items: center;\n    justify-content: center;\n    background: #1B2A4A;\n    color: #FFFFFF;\n    font-size: 11pt;\n    font-weight: 700;\n    width: 28pt;\n    height: 28pt;\n    border-radius: 4pt;\n    flex-shrink: 0;\n  }\n\n  .section-title {\n    font-size: 16pt;\n    font-weight: 700;\n    color: #111827;\n    line-height: 1.2;\n  }\n\n  .subsection {\n    margin-top: 0;\n    margin-bottom: 10pt;\n  }\n\n  /* ========================================\n     CARD COMPONENTS - Minimal, clean\n     ======================================== */\n  .card {\n    background: #FAFAFA;\n    border: 0.5pt solid #E5E7EB;\n    border-radius: 4pt;\n    padding: 10pt 12pt;\n    margin-bottom: 8pt;\n  }\n\n  .card-header {\n    margin-bottom: 6pt;\n    padding-bottom: 4pt;\n    border-bottom: 0.5pt solid #E5E7EB;\n  }\n\n  .card-title {\n    font-size: 11pt;\n    font-weight: 600;\n    color: #1F2937;\n  }\n\n  .card-grid {\n    display: grid;\n    grid-template-columns: repeat(2, 1fr);\n    gap: 8pt;\n    margin: 8pt 0;\n  }\n\n  .card-grid-3 {\n    display: grid;\n    grid-template-columns: repeat(3, 1fr);\n    gap: 8pt;\n    margin: 8pt 0;\n  }\n\n  /* ========================================\n     BADGES & INDICATORS\n     ======================================== */\n  .badge {\n    display: inline-block;\n    padding: 2pt 7pt;\n    border-radius: 3pt;\n    font-size: 8pt;\n    font-weight: 600;\n    margin-right: 4pt;\n  }\n\n  .badge-primary {\n    background: #EFF6FF;\n    color: #1E40AF;\n  }\n\n  .badge-success {\n    background: #ECFDF5;\n    color: #065F46;\n  }\n\n  .badge-warning {\n    background: #FFFBEB;\n    color: #92400E;\n  }\n\n  .badge-info {\n    background: #EEF2FF;\n    color: #3730A3;\n  }\n\n  .badge-red {\n    background: #FEF2F2;\n    color: #991B1B;\n  }\n\n  .demand-indicator {\n    display: inline-block;\n    padding: 1pt 8pt;\n    border-radius: 3pt;\n    font-size: 8pt;\n    font-weight: 600;\n  }\n\n  .demand-very-high {\n    background: #059669;\n    color: #FFFFFF;\n  }\n\n  .demand-high {\n    background: #10B981;\n    color: #FFFFFF;\n  }\n\n  .demand-medium {\n    background: #F59E0B;\n    color: #FFFFFF;\n  }\n\n  /* ========================================\n     TABLES - Compact, professional\n     ======================================== */\n  table {\n    width: 100%;\n    border-collapse: collapse;\n    margin: 6pt 0;\n    font-size: 9pt;\n    page-break-inside: auto;\n  }\n\n  thead {\n    background: #F8F9FA;\n  }\n\n  thead th {\n    padding: 7pt 8pt;\n    text-align: left;\n    font-weight: 600;\n    color: #374151;\n    border-bottom: 1.5pt solid #D1D5DB;\n    font-size: 9pt;\n    text-transform: uppercase;\n    letter-spacing: 0.02em;\n  }\n\n  tbody td {\n    padding: 6pt 8pt;\n    border-bottom: 0.5pt solid #E5E7EB;\n    vertical-align: top;\n    line-height: 1.4;\n    color: #374151;\n  }\n\n  tbody tr {\n    page-break-inside: avoid;\n    page-break-after: auto;\n  }\n\n  tbody tr:nth-child(even) {\n    background: #FAFAFA;\n  }\n\n  tbody tr:nth-child(odd) {\n    background: #FFFFFF;\n  }\n\n  /* ========================================\n     LISTS - Tight spacing\n     ======================================== */\n  ul, ol {\n    margin-left: 14pt;\n    margin-bottom: 6pt;\n  }\n\n  li {\n    margin-bottom: 3pt;\n    line-height: 1.4;\n  }\n\n  ul.checkmark-list {\n    list-style: none;\n    margin-left: 0;\n  }\n\n  ul.checkmark-list li {\n    margin-bottom: 4pt;\n  }\n\n  ul.checkmark-list li:before {\n    content: \"\\2713  \";\n    color: #059669;\n    font-weight: 700;\n    margin-right: 4pt;\n  }\n\n  /* ========================================\n     TABLE OF CONTENTS\n     ======================================== */\n  .toc {\n    margin: 20pt 0;\n  }\n\n  .toc-title {\n    font-size: 22pt;\n    font-weight: 700;\n    color: #111827;\n    margin-bottom: 16pt;\n  }\n\n  .toc-item {\n    display: flex;\n    justify-content: space-between;\n    padding: 6pt 0;\n    border-bottom: 0.5pt dotted #D1D5DB;\n    font-size: 10pt;\n  }\n\n  .toc-item-title {\n    flex: 1;\n    color: #374151;\n  }\n\n  .toc-item-page {\n    color: #6B7280;\n    font-weight: 600;\n    margin-left: 12pt;\n  }\n\n  /* ========================================\n     VISA PATHWAY CARDS\n     ======================================== */\n  .visa-pathway {\n    background: #FFFFFF;\n    border: 0.5pt solid #E5E7EB;\n    border-left: 3pt solid #1B2A4A;\n    padding: 10pt 12pt;\n    margin-bottom: 8pt;\n    border-radius: 2pt;\n  }\n\n  .visa-pathway-title {\n    font-size: 12pt;\n    font-weight: 600;\n    color: #111827;\n    margin-bottom: 4pt;\n  }\n\n  .visa-pathway-subclass {\n    font-size: 9pt;\n    color: #6B7280;\n    margin-bottom: 6pt;\n  }\n\n  .visa-requirement {\n    margin-bottom: 3pt;\n    font-size: 9pt;\n  }\n\n  .visa-requirement-label {\n    font-weight: 600;\n    color: #374151;\n  }\n\n  .visa-requirement-value {\n    color: #4B5563;\n  }\n\n  /* ========================================\n     TIMELINE\n     ======================================== */\n  .timeline {\n    margin: 8pt 0;\n  }\n\n  .timeline-phase {\n    position: relative;\n    padding-left: 24pt;\n    margin-bottom: 10pt;\n    border-left: 2pt solid #1B2A4A;\n    page-break-inside: avoid;\n    padding: 8pt 10pt 8pt 24pt;\n  }\n\n  .timeline-phase:before {\n    content: \"\";\n    position: absolute;\n    left: -6pt;\n    top: 10pt;\n    width: 10pt;\n    height: 10pt;\n    background: #1B2A4A;\n    border-radius: 50%;\n    border: 2pt solid #FFFFFF;\n  }\n\n  .timeline-phase-name {\n    font-size: 11pt;\n    font-weight: 600;\n    color: #1F2937;\n    margin-bottom: 3pt;\n  }\n\n  .timeline-phase-duration {\n    font-size: 9pt;\n    color: #6B7280;\n    margin-bottom: 4pt;\n  }\n\n  .timeline-steps {\n    list-style: none;\n    margin-left: 0;\n  }\n\n  .timeline-steps li {\n    padding-left: 12pt;\n    margin-bottom: 2pt;\n    position: relative;\n    font-size: 12pt;\n    color: #4B5563;\n  }\n\n  .timeline-steps li:before {\n    content: \"\\2192\";\n    position: absolute;\n    left: 0;\n    color: #1B2A4A;\n    font-weight: 700;\n  }\n\n  /* ========================================\n     COMPANY LIST & CARDS\n     ======================================== */\n  .company-tier {\n    margin-bottom: 12pt;\n    page-break-inside: avoid;\n  }\n\n  .company-tier-header {\n    background: #F8F9FA;\n    padding: 6pt 10pt;\n    margin-bottom: 6pt;\n    border-left: 3pt solid #1B2A4A;\n    border-radius: 2pt;\n  }\n\n  .company-tier-title {\n    font-size: 11pt;\n    font-weight: 600;\n    color: #111827;\n    margin-bottom: 2pt;\n  }\n\n  .company-tier-description {\n    font-size: 9pt;\n    color: #6B7280;\n    margin-top: 2pt;\n  }\n\n  .company-list {\n    list-style: none;\n    margin-left: 0;\n    display: grid;\n    grid-template-columns: repeat(2, 1fr);\n    gap: 6pt;\n  }\n\n  .company-item {\n    padding: 6pt 8pt;\n    background: #FFFFFF;\n    border: 0.5pt solid #E5E7EB;\n    border-radius: 3pt;\n    page-break-inside: avoid;\n  }\n\n  .company-name {\n    font-weight: 600;\n    color: #1F2937;\n    font-size: 9.5pt;\n    display: block;\n    margin-bottom: 2pt;\n  }\n\n  .company-location {\n    font-size: 8pt;\n    color: #6B7280;\n    background: #F3F4F6;\n    padding: 1pt 5pt;\n    border-radius: 2pt;\n    display: inline-block;\n    margin-bottom: 3pt;\n  }\n\n  .company-description {\n    font-size: 8.5pt;\n    color: #4B5563;\n    margin-top: 2pt;\n    line-height: 1.3;\n  }\n\n  /* ========================================\n     SALARY VARIATION\n     ======================================== */\n  .salary-variation-intro {\n    font-size: 10pt;\n    color: #475569;\n    margin-bottom: 10pt;\n    line-height: 1.5;\n  }\n\n  .city-card {\n    background: #FFFFFF;\n    border: 0.5pt solid #E5E7EB;\n    border-left: 3pt solid #1B2A4A;\n    border-radius: 3pt;\n    padding: 10pt 12pt;\n    margin-bottom: 8pt;\n    page-break-inside: avoid;\n  }\n\n  .city-card-header {\n    margin-bottom: 6pt;\n    padding-bottom: 4pt;\n    border-bottom: 0.5pt solid #E5E7EB;\n  }\n\n  .city-card-header-content {\n    flex: 1;\n  }\n\n  .city-name {\n    font-size: 12pt;\n    font-weight: 600;\n    color: #111827;\n  }\n\n  .city-card-subtitle {\n    font-size: 8pt;\n    color: #6B7280;\n    margin-top: 2pt;\n    margin-bottom: 0;\n  }\n\n  .city-card-salary-block {\n    margin-bottom: 8pt;\n    padding: 6pt 8pt;\n    background: #FAFAFA;\n    border-radius: 3pt;\n  }\n\n  .salary-range {\n    margin: 4pt 0;\n  }\n\n  .salary-range-row {\n    display: flex;\n    align-items: center;\n    gap: 8pt;\n  }\n\n  .salary-label {\n    font-size: 9pt;\n    font-weight: 600;\n    color: #475569;\n    display: block;\n    margin-bottom: 2pt;\n  }\n\n  .salary-value {\n    font-size: 10pt;\n    font-weight: 700;\n    color: #1B2A4A;\n  }\n\n  .salary-premium-badge {\n    background: #EFF6FF;\n    color: #1E40AF;\n    border: 0.5pt solid #BFDBFE;\n    padding: 1pt 6pt;\n    border-radius: 2pt;\n    font-size: 7.5pt;\n    font-weight: 600;\n  }\n\n  .salary-bar {\n    height: 6pt;\n    background: #E5E7EB;\n    border-radius: 3pt;\n    margin: 3pt 0;\n    overflow: hidden;\n  }\n\n  .salary-bar-fill {\n    height: 100%;\n    background: #1B2A4A;\n    border-radius: 3pt;\n  }\n\n  .city-details {\n    display: grid;\n    grid-template-columns: repeat(2, 1fr);\n    gap: 4pt 12pt;\n    margin-top: 6pt;\n  }\n\n  .city-detail-item {\n    font-size: 8.5pt;\n  }\n\n  .city-detail-label {\n    color: #6B7280;\n    margin-bottom: 1pt;\n  }\n\n  .city-detail-value {\n    font-weight: 600;\n    color: #1F2937;\n  }\n\n  .city-detail-value-accent {\n    color: #1B2A4A;\n  }\n\n  .city-notes {\n    margin-top: 6pt;\n    padding: 6pt 8pt;\n    background: #F0F4F8;\n    border-left: 2pt solid #1B2A4A;\n    border-radius: 0 3pt 3pt 0;\n  }\n\n  .city-notes-text {\n    font-size: 8.5pt;\n    color: #374151;\n    margin-bottom: 0;\n    line-height: 1.4;\n  }\n\n  /* ========================================\n     THANK YOU PAGE\n     ======================================== */\n  .thank-you-page {\n    padding: 10pt;\n    text-align: center;\n    min-height: 100vh;\n    display: flex;\n    flex-direction: column;\n    justify-content: center;\n  }\n\n  .thank-you-logo-container {\n    text-align: center;\n    margin-bottom: 24pt;\n    width: 100%;\n  }\n\n  .thank-you-logo {\n    max-width: 200pt;\n    max-height: 80pt;\n    height: auto;\n    width: auto;\n    object-fit: contain;\n  }\n\n  .about-worldvisa-logo {\n    max-width: 20pt;\n    max-height: 20pt;\n    height: auto;\n    width: auto;\n    object-fit: contain;\n    flex-shrink: 0;\n  }\n\n  .thank-you-hero {\n    padding: 20pt;\n    margin-bottom: 16pt;\n  }\n\n  .thank-you-heading {\n    font-size: 22pt;\n    font-weight: 700;\n    color: #111827;\n    margin-bottom: 10pt;\n    line-height: 1.2;\n  }\n\n  .thank-you-message {\n    font-size: 10pt;\n    color: #4B5563;\n    margin-bottom: 16pt;\n    line-height: 1.6;\n  }\n\n  .about-worldvisa {\n    text-align: left;\n    margin: 12pt 0;\n    padding: 14pt;\n    background: #FFFFFF;\n    border: 0.5pt solid #E5E7EB;\n    border-radius: 4pt;\n  }\n\n  .about-worldvisa h3 {\n    color: #111827;\n    margin-top: 0;\n    font-size: 13pt;\n  }\n\n  .contact-info {\n    margin: 12pt 0;\n    text-align: left;\n    background: #FAFAFA;\n    padding: 14pt;\n    border-radius: 4pt;\n  }\n\n  .contact-item {\n    margin-bottom: 6pt;\n    font-size: 10pt;\n    display: flex;\n    align-items: center;\n  }\n\n  .cta-button {\n    display: inline-block;\n    background: #1B2A4A;\n    color: #FFFFFF !important;\n    padding: 10pt 32pt;\n    font-size: 12pt;\n    font-weight: 600;\n    text-decoration: none;\n    border-radius: 4pt;\n    margin: 16pt 0;\n  }\n\n  .cta-button:visited {\n    color: #FFFFFF !important;\n  }\n\n  .footer-text {\n    margin-top: 20pt;\n    font-size: 8pt;\n    color: #9CA3AF;\n  }\n\n  .trust-indicators {\n    display: grid;\n    grid-template-columns: repeat(3, 1fr);\n    gap: 8pt;\n    margin: 12pt 0;\n  }\n\n  .trust-item {\n    text-align: center;\n    padding: 10pt;\n    background: #FAFAFA;\n    border-radius: 4pt;\n  }\n\n  .trust-number {\n    font-size: 20pt;\n    font-weight: 700;\n    color: #1B2A4A;\n    display: block;\n    margin-bottom: 2pt;\n  }\n\n  .trust-label {\n    font-size: 8pt;\n    color: #6B7280;\n  }\n\n  /* ========================================\n     UTILITY CLASSES\n     ======================================== */\n  .text-center { text-align: center; }\n  .text-right { text-align: right; }\n  .mb-small { margin-bottom: 4pt; }\n  .mb-medium { margin-bottom: 8pt; }\n  .mb-large { margin-bottom: 16pt; }\n  .color-primary { color: #D52636; }\n  .color-success { color: #059669; }\n  .color-gray { color: #6B7280; }\n\n  /* ========================================\n     PAGE NUMBERING\n     ======================================== */\n  @page {\n    margin: 12mm 12mm 16mm 12mm;\n\n    @bottom-center {\n      content: \"Page \" counter(page);\n      font-size: 8pt;\n      color: #9CA3AF;\n      font-family: 'Segoe UI', -apple-system, BlinkMacSystemFont, Helvetica, Arial, sans-serif;\n    }\n  }\n\n  .cover-page { page: cover; }\n  @page cover {\n    margin: 0;\n    @bottom-center { content: none; }\n  }\n\n  .table-of-contents { page: toc; }\n  @page toc {\n    @bottom-center { content: none; }\n  }\n\n  /* ========================================\n     PRINT OPTIMIZATION\n     ======================================== */\n  @media print {\n    .page-break { page-break-after: always; }\n    .no-print { display: none; }\n    a { text-decoration: none; color: inherit; }\n    .section { page-break-inside: avoid; }\n    table { page-break-inside: auto; }\n    tbody tr { page-break-inside: avoid; }\n    .visa-pathway { page-break-inside: avoid; }\n    .company-tier { page-break-inside: avoid; }\n    .city-card { page-break-inside: avoid; }\n  }\n";
